package org.osgi.service.zigbee;

import java.util.Dictionary;

/**
 * This interface represents events generated by a ZigBee Device node
 *  
 * @version 1.0
 */
public interface ZigBeeEvent {
	
	/**
	 * @return The cluster object concerned by the events
	 */
	public ZigBeeCluster getCluster();
	
	/**
	 * @return A dictionary containing new values for the ZigBee attributes that have changed.
	 */
	public Dictionary getAttributesEvents();
	
}
