public class B implements Comparable {
    String keys[] = {"bugs", "daffy", "elmer", "pepe"};
    int         index;

    public B(String s) {
        for ( index=0; index<keys.length; index++ ) 
            if ( keys[index].equals(s) )
                return;
    }

    public int compareTo( Object other ) {
        B vother = (B) other;
        return index - vother.index;
    }
 }

