public class GroupHandler implements ResolverHook{
    Map<Bundle,Set<Bundle>> groups = ...;
    Set<Bundle> system = ... ;

    public void filterMatches( BundleRequirement r,
        Collection<BundleCapability> candidates ) {
        Set<Bundle> group = groups.get(r.getRevision().getBundle());
        if ( group == null )
            return; // not in a group

        for ( Iterator<BundleCapability> i = candidates.iterator();
            i.hasNext(); ) {
            BundleCapability candidate = i.next();
            Bundle other = candidate.getRevision().getBundle();

            if ( group.contains(other) || 
                system.contains(other) )
                continue;

            i.remove(); // not system, not in the same group
        }
    }

   ... other methods
}

public class GroupHandler implements ResolverHook{
    Map<Bundle,Set<Bundle>> groups = ...;

    public void filterSingletonCollisions( BundleCapability c,
        Collection<BundleCapability> candidates ) {
        Set<Bundle> group = groups.get(c.getRevision().getBundle());

        for ( Iterator<BundleCapability> i = candidates.iterator();
            i.hasNext(); ) {
            BundleCapability candidate = i.next();
            Bundle other = candidate.getRevision().getBundle();
            Set<Bundle> otherGroup = groups.get(other);

            if ( group == otherGroup || otherGroup == null ) // Samegroup
                continue;

            i.remove(); // not system, not in the same group
        }
    }

   ... other methods
}

public class Hide implementsEventListenerHook, FindHook {
    final Bundle           bundle;
    final ServiceReference reference;
    final BundleContext    context;
    ServiceRegistration    reg;

    Hide(BundleContext context, 
        ServiceReference reference, 
        Bundle bundle) {
        this.context = context;
        this.bundle = bundle;
        this.reference = reference;
    }

    void open() {
        boolean active = 
            bundle.getBundleState() == Bundle.ACTIVE;
        if ( active )
            bundle.stop(Bundle.STOP_TRANSIENTLY);
        reg = context.registerService(
            new String[] {
                FindHook.class.getName(),
                EventListenerHook.class.getName() }, this, null);
        if ( active )
            bundle.start(Bundle.START_TRANSIENTLY);
    }

    public void close() { reg.unregister();}

public void find(BundleContext ctx, 
    String name, String filter,
    boolean allServices, Collection refs) {
    if (ctx.getBundle() == bundle) {
        refs.remove(reference);
    }
}

    public void event(ServiceEvent event, 
        Collection bundles) {
        if (event.getServiceReference().equals(
            reference)) 
            bundles.remove(bundle);
    }
}

